import React from 'react';

import TimeTable from './TimeTable.jsx';
import DayTable from './DayTable.jsx';

import './ScheduleViewer.scss';

export default class ScheduleViewer extends React.Component {
    constructor(props) {
        super(props);

        this.schedule = this.parseSchedule(props.schedule);

        this.state = {
            display_schedule : this.getDisplaySchedule(this.schedule)
        };
    }

    parseSchedule(schedule_arr) {
        var schedule = {};

        for (let i = 1; i < schedule_arr.length; ++i) {
            if ( schedule_arr[i][1][0] !== ' ' ) {
                schedule_arr[i][1][0] = ' ' + schedule_arr[i][1][0];
            }

            let course = schedule_arr[i][0] + schedule_arr[i][1];

            let days_string = String(schedule_arr[i][3]);
            let days_array  = {}

            while ( days_string !== '' ) {
                if ( days_string[0] === 'M' ) {
                    days_array['Monday'] = true;
                    days_string = days_string.substring(1);
                }
                else if ( days_string[0] === 'W' ) {
                    days_array['Wednesday'] = true;
                    days_string = days_string.substring(1);
                }
                else if ( days_string[0] === 'F' ) {
                    days_array['Friday'] = true;
                    days_string = days_string.substring(1);
                }
                else if ( days_string.substring(0,2) === 'TH' ) {
                    days_array['Thursday'] = true;
                    days_string = days_string.substring(2);
                }
                else if ( days_string[0] === 'T' ) {
                    days_array['Tuesday'] = true;
                    days_string = days_string.substring(1);
                }
                else {
                    break;
                }
            }

            let is_afternoon = false;
            let time         = {}
            let time_string  = schedule_arr[i][4];
            let time_modify  = schedule_arr[i][4].slice(-2);
            let duration     = schedule_arr[i][6];

            if ( time_modify === 'PM' ) {
                is_afternoon = true;
            }







            schedule[course]               = {}
            schedule[course]['course']     = course;
            schedule[course]['days']       = days_array;
            schedule[course]['time']       = '';
            schedule[course]['room']       = schedule_arr[i][2];
            schedule[course]['instructor'] = schedule_arr[i][5];
        }

        return schedule;
    }

    getDisplaySchedule(schedule) {
        var display_schedule = {};

        for (var key in schedule) {
            console.log(schedule[key]);
        }

        return display_schedule;
    }

    render() {
        return (
            <>
              <div className="cs-table-header-wrapper">
                <div className="cs-timetable-header">
                  <a className="cs-table-filter" href="# ">
                    <i className="fas fa-filter"></i>
                  </a>
                </div>

                <div className="cs-daytable-header">Monday   </div>
                <div className="cs-daytable-header">Tuesday  </div>
                <div className="cs-daytable-header">Wednesday</div>
                <div className="cs-daytable-header">Thursday </div>
                <div className="cs-daytable-header">Friday   </div>
                <div className="cs-daytable-header">Saturday </div>
                <div className="cs-daytable-header">Sunday   </div>
              </div>

              <div className="cs-table-wrapper">
                <TimeTable />

                <DayTable day={'Monday'}    />
                <DayTable day={'Tuesday'}   />
                <DayTable day={'Wednesday'} />
                <DayTable day={'Thursday'}  />
                <DayTable day={'Friday'}    />
                <DayTable day={'Saturday'}  />
                <DayTable day={'Sunday'}    />
              </div>
            </>
        );
    }
}
