import React from 'react';

import ScheduleViewer from '../ScheduleViewer/ScheduleViewer.jsx';
import PacmanLoad from 'react-spinners/PacmanLoader';

//import { readString } from 'react-papaparse';

import './Scheduler.scss';


export default class Scheduler extends React.Component {
    constructor(props) {
        super(props);

        this.courses_file     = props.courses;
        this.instructors_file = props.instructors;

        this.courses     = null;
        this.instructors = null;
        this.program     = null;

        this.state = {
            schedule: null
        };
    }

    componentDidMount() {
        this.run_scheduler();
    }

    run_scheduler() {
        let _this = this;

        if ( this.courses_file ) {
            let courses_reader = new FileReader();
            courses_reader.onload = function(e) {
                this.courses = courses_reader.result;
                _this.received_file();
            }
            courses_reader.readAsText( this.courses_file );
        }
        else {
            // error...
        }

        if ( this.instructors_file ) {
            let instructors_reader = new FileReader();
            instructors_reader.onload = function(e) {
                this.instructors = instructors_reader.result;
                _this.received_file();
            }
            instructors_reader.readAsText( this.instructors_file );
        }
    }

    received_file() {
        let _this = this;
        if ( this.courses_file && this.courses ) {
            window.jQuery.get('./main.py', function(program) {
                this.program = program;

                // Create global data for courses and instructors and append
                let prog = 'from six import StringIO\nTESTDATA = StringIO("""' + this.courses + '""")\n';

                window.languagePluginLoader.then(() => {
                    window.pyodide.loadPackage('pandas').then(() => {
                        this.setState({
                            schedule: window.pyodide.runPython( prog + this.program )
                        });
                    });
                });
            });
        }
    }

    render() {
        return (
            <>
                { this.state.schedule ?

                    <ScheduleViewer schedule = {this.state.schedule} />

                :

                    <div className="sv-loading">
                        <PacmanLoad size='50px' color='white' />
                    </div>
                }
            </>
        );
    }
}
