import React from 'react';
import ScheduleViewer from './ScheduleViewer/ScheduleViewer.jsx'

import './CourseScheduling.scss';

export default class CourseScheduling extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      courses: null,
      instructors: null
    }
  }

  verifyUpload(ref) {
    alert(ref);
  }

  render() {
    return (
      <>
        { Header() }

        { this.state.courses && this.state.instructors ?

          <ScheduleViewer courses = {this.state.courses} instructors = {this.state.instructors} />

        : StartPage() }
      </>
    );
  }
}

function Header() {
  return (
    <>
      <h1 className="cs-header">
        <a href="/">Course Scheduling</a>
      </h1>

      <div className="cs-project-info">
        <p className="cs-authors">
          Authors: Jonathan Kelaty, Manal Zneit
        </p>

        <p className="cs-project-link">
          <a href="https://github.com/jkelaty/course-scheduling">GitHub Repo</a>
        </p>
      </div>
    </>
  );
}

function StartPage() {
  return (
    <>
      <div className="cs-info-wrapper">
        <p className="cs-info">
          This is a supplemental visualization tool for our CSCI 350/761 Artificial
          Intelligence final project. Our project addresses the problem of course
          scheduling for universities as a Constraint Satisfaction Problem. Our
          objective when designing the interface for this project was to create a
          tool that can be practically used using readily available information
          provided by any school. To use this tool, simply upload a CSV with course
          information and optionally a CSV containing instructor's preferences for
          certain scheduling parameters. Sample CSVs and templates can be found below.
        </p>

        <div className="cs-upload">
          <form>
            <label for="courses">Courses: </label>
            <input onChange={(ref) => this.verifyUpload(ref)} type="file" id="courses" name="courses" accept=".csv" />

            <br />
            <br />

            <a className="cs-file-download-link" href="./templates/courses.csv" download>Template CSV</a>
            <br />
            <a className="cs-file-download-link" href="./templates/courses.csv" download>Sample CSV</a>

            <br />
            <br />

            <label for="instructors">Instructors: </label>
            <input onChange={(ref) => this.verifyUpload(ref)} type="file" id="instructors" name="instructors" accept=".csv" />

            <br />
            <br />

            <a className="cs-file-download-link" href="./templates/courses.csv" download>Template CSV</a>
            <br />
            <a className="cs-file-download-link" href="./templates/courses.csv" download>Sample CSV</a>

            <br />
            <br />

            <input type="submit" disabled/>
          </form>
        </div>
      </div>
    </>
  );
}
