import React from 'react';

import WeeklySchedule from './WeeklySchedule.jsx';
import PacmanLoad from 'react-spinners/PacmanLoader';

import './ScheduleViewer.scss';

export default class ScheduleViewer extends React.Component {
    constructor(props) {
        super(props);

        this.courses     = props.courses;
        this.instructors = props.instructors;

        this.state = {
            schedule: null
        };

        this.run_scheduler()
    }

    run_scheduler() {
        let program = readTextFile('./main.py');
        window.languagePluginLoader.then(() => {
            window.pyodide.loadPackage(['pandas']).then(() => {
                console.log( window.pyodide.runPython(program) );
            });
          });
    }

    render() {
        return (
            <>
                { this.state.schedule ?

                    <WeeklySchedule />

                :

                    <div className="sv-loading">
                        <PacmanLoad size='50px' color='white' />
                    </div>
                }
            </>
        );
    }
}

function readTextFile(file) {
    var rawFile = new XMLHttpRequest();
    var allText = '';
    rawFile.open("GET", file, false);
    rawFile.onreadystatechange = function () {
        if (rawFile.readyState === 4) {
            if( rawFile.status === 200 || rawFile.status == 0) {
                allText = rawFile.responseText;
            }
        }
    }
    rawFile.send(null);
    return allText;
}
